import { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = () => {

  const [title, setTitle] = useState('')
  const [amount, setAmount] = useState('')
  const [date, setDate ] = useState('')

  const eventHandlerTitle = (event) => {
    setTitle(event.target.value)
  }
  const eventHandlerAmount = (event) => {
    setAmount(event.target.value)
  }
  const eventHandlerDate = (event) => {
    setDate(event.target.value)
  }

  const newExpenses = (event) => {
    event.preventDefault()

    const expenses = 
    {
    title: title,
    amount: amount,
    date: date
    }
    setTitle('')
    setAmount('')
    setDate('')

    console.log(expenses.title)
    console.log(expenses.amount)
    console.log(expenses.date)
  }




  return (
    <form onSubmit={newExpenses}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            onChange={eventHandlerTitle}
            value={title}
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            onChange={eventHandlerAmount}
            value={amount}
            type="number"
            min="0.01"
            step="0.01"
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            onChange={eventHandlerDate}
            value={date}
            type="date"
            min="2019-01-01"
            max="2022-12-31"
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
